/**
 * This is equivalent of saying find all the test files,
 * and call require on each dynamically so as to make sure they are
 * all within the bundle generated by tests.
 *
 * Thanks to https://github.com/vanessayuenn for the code
 */
var testContext = require.context('./src', true, /\.test\.js/);

testContext.keys().forEach(testContext);
